import './App.css';
import {useState,useEffect} from 'react'
import axios from 'axios'
import Button from '@material-ui/core/Button';
import TextField from '@material-ui/core/TextField';
import Dialog from '@material-ui/core/Dialog';
import DialogActions from '@material-ui/core/DialogActions';
import DialogContent from '@material-ui/core/DialogContent';
import DialogContentText from '@material-ui/core/DialogContentText';
import DialogTitle from '@material-ui/core/DialogTitle';
import {useHistory}  from 'react-router-dom'



axios.defaults.withCredentials = true

function App() {
  const [loggedIn, setLoggedIn] = useState();

  const [email,setemail] = useState("")
  const [password,setpassword] = useState("")
  const [passwordverify,setpasswordverify] = useState("")
  const history = useHistory()



  const [openlogin, setOpenlogin] = useState(false);

  const handleClickOpenlogin = () => {
    setOpenlogin(true);
  };

  const handleCloselogin = () => {
    setOpenlogin(false);
  };





  const [opensign, setOpensign] = useState(false);

  const handleClickOpensign = () => {
    setOpensign(true);
  };

  const handleClosesign = () => {
    setOpensign(false);
  };



const getauth = async() => {

  await axios.get("http://localhost:5100/loggedIn").then((res) => {
    setLoggedIn(res.data)
  })


}


useEffect(() => { 
  getauth()
},[])



const handlesigiin = (e) => {
  e.preventDefault();

  const newuser = {
    email : email,
    password : password,
    passwordverify : passwordverify
  }
   axios.post("http://localhost:5100/sigin",newuser).then(() => {
     console.log("data alindi")
   })
   window.location.reload()
}

const logout = async(e) => {
  e.preventDefault()

  try{
    axios.get("http://localhost:5100/logout").then(() => console.log("user deleting success"))
  }catch(err){
    console.log(err)
  } 





}


const handlelogiin = async(e) => {
e.preventDefault()
try{
  const user = {
    email : email,
    password : password
  }
  axios.post("http://localhost:5100/login",user).then(res => {
    console.log(res)
  })
}catch(err){
  console.log(err)
}

}






  return (
    <div className="App">
      <div className = "allforum">

        {
          loggedIn ? (
            <div style = {{textAlign : "center",marginTop : "20px"}}>
              <button className  = "btn btn-danger" onClick = {logout}>Log out</button>
            </div>
          ) : (
            <div>
                <p className = "text1">please regitser or create new account</p>
              <div className = "all-btn">

                <br/>
                <button className = "btn btn-success" style = {{marginRight : "20px"}} onClick = {handleClickOpenlogin}>Login</button>
                <button className = "btn btn-primary" onClick = {handleClickOpensign}>Sign in</button>
              </div>

            </div>
          )
        }



{/* login!!!! */}


<Dialog open={openlogin} onClose={handleCloselogin} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Login</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email"
            type="email"
            fullWidth
            onChange = {(e) => {setemail(e.target.value)}}
          />
          <br/>
          <br/>
              <TextField
            autoFocus
            margin="dense"
            id="name"
            label="password"
            type="password"
            fullWidth
            onChange = {(e) => {setpassword(e.target.value)}}
          />
        </DialogContent>
        <DialogActions>
          <Button onClick={handleCloselogin} color="primary">
            Cancel
          </Button>
          <Button onClick={handlelogiin} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>







          {/* Sign!!!! */}


<Dialog open={opensign} onClose={handleClosesign} aria-labelledby="form-dialog-title">
        <DialogTitle id="form-dialog-title">Sign in</DialogTitle>
        <DialogContent>
          <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Email"
            type="email"
            fullWidth
            onChange = {(e) => {setemail(e.target.value)}}
          />
          <br/>
          <br/>
            <TextField
            autoFocus
            margin="dense"
            id="name"
            label="Password"
            type="password"
            fullWidth
            onChange = {(e) => {setpassword(e.target.value)}}
          />
          <br/>
          <br/>
           <TextField
            autoFocus
            margin="dense"
            id="name"
            label="passwordverify"
            type="password"
            fullWidth
            onChange = {(e) => {setpasswordverify(e.target.value)}}
          />
          <br/>
        </DialogContent>
        <DialogActions>
          <Button onClick={handleClosesign} color="primary">
            Cancel
          </Button>
          <Button onClick={handlesigiin} color="primary">
            Subscribe
          </Button>
        </DialogActions>
      </Dialog>








      </div>
    </div>
  );
}

export default App;
